package com.lao.String;

public class Stringmethods {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String data ="Manikandan";
		int dob = 10211989;
		
		System.out.println(data.charAt(2));
		System.out.println(data.valueOf(dob));// different datatype to String//
		System.out.println(data.equalsIgnoreCase("Manikandan"));

	}

}
